mapper.update:
UPDATE  t_sys_menu
SET  status='UN_NORMAL',update_date=NOW()
WHERE  id=#{id}

mapper.select:
SELECT COUNT(*)
FROM  t_sys_menu
WHERE  menu_name=#{menuName} AND status='NORMAL' AND url=#{url}

SELECT COUNT(*)
FROM t_sys_menu
WHERE url =#{url}
AND menu_name =#{menuName}
AND id!=#{id}
AND status='NORMAL'
